mapper.update:
update user
set paw = #{paw} , paw_state = 1
where id = #{id}

mapper.select:
select `name` , u.id , pid , paw_state as pawState , tid ,
GROUP_CONCAT(up.position_id) as `position`
from `user` u left join
user_position up on up.user_id = u.id
where u.username = #{username}
and u.paw = #{paw}

select u.id , u.name , u.username , u.grade , d.name as department , t.name as technology , p.name as power from `user` u, technology t , power p , department d where u.pid = p.id and u.tid = t.id and u.did = d.id
and u.name like "%"#{name}"%"
and u.username like "%"#{username}"%"
and d.id in
#{item}
and t.id in
#{item}
order by ${selectName}
desc

select id , `name` , username
from  `user`
where tid = #{id}

select distinct u.id , u.name as name, u.username , d.name as department , t.name as technology , us.designer , us.personal , us.coordinate from `user` u left join user_score us on us.grade_id = #{id} and u.id = us.score_id , department d, technology t where u.id <> #{id} and u.did = d.id and u.tid = t.id
and d.id in
#{item}
and t.id in
#{item}
order by ${selectName}
desc

